import React, { useState } from 'react';
import Layout from './Layout';
import CreateQRModal from './CreateQRModal';
import QRCodeCard from './QRCodeCard';
import EditQRModal from './EditQRModal';
import ConfirmDeleteModal from './ConfirmDeleteModal';

const Dashboard = () => {

  //
  //States
  //
  const [qrCodes, setQrCodes] = useState([
    {
      id: 1,
      name: 'Morant',
      url: 'https://morant.com.mx',
      codeUrl: 'qr.codes/cKmY7j',
      scans: 3
    }
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [qrToEdit, setQrToEdit] = useState(null);
  const [qrToDelete, setQrToDelete] = useState(null);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);

  //
  //Handlers
  //
  const handleCreate = (newQR) => {
    const newQrCode = {
      id: qrCodes.length + 1,
      name: newQR.name,
      url: newQR.url,
      fgColor: newQR.fgColor,
      bgColor: newQR.bgColor,
      size: newQR.size,
      scans: 0
    };
    setQrCodes([...qrCodes, newQrCode]);
    console.log('Nuevo QR:', newQR);
  };

  const handleEdit = (qr) => {
    setQrToEdit(qr);
    setIsEditModalOpen(true);
  };

  const handleUpdate = (updatedQR) => {
    setQrCodes(qrCodes.map((qr) => (qr.id === updatedQR.id ? updatedQR : qr)));
  };

  const confirmDelete = (qr) => {
    setQrToDelete(qr);
    setIsDeleteModalOpen(true);
  };

  const handleDeleteConfirmed = () => {
    setQrCodes(qrCodes.filter((qr) => qr.id !== qrToDelete.id));
    setIsDeleteModalOpen(false);
    setQrToDelete(null);
  };


  //
  // JSX
  //
  return (
    <Layout>
      <div>
        <div className="flex justify-between items-center mb-6">
          <CreateQRModal
            isOpen={isModalOpen}
            onClose={() => setIsModalOpen(false)}
            onCreate={handleCreate}
          />
          <EditQRModal
            isOpen={isEditModalOpen}
            onClose={() => setIsEditModalOpen(false)}
            onUpdate={handleUpdate}
            qrToEdit={qrToEdit}
          />
          <ConfirmDeleteModal
            isOpen={isDeleteModalOpen}
            onCancel={() => setIsDeleteModalOpen(false)}
            onConfirm={handleDeleteConfirmed}
            qrName={qrToDelete?.name}
          />
          <h1 className="text-3xl font-bold text-green-700">Active QR Codes</h1>
          <button
            onClick={() => setIsModalOpen(true)}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
          >
            + Crear QR Code
          </button>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {qrCodes.map((qr) => (
            <QRCodeCard
              key={qr.id}
              name={qr.name}
              url={qr.url}
              codeUrl={qr.codeUrl}
              scans={qr.scans}
              onEdit={() => handleEdit(qr)}
              onDelete={() => confirmDelete(qr)}
            />
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
