import React, { useState } from 'react';
import Layout from './Layout';
import CreateQRModal from './CreateQRModal';

const Dashboard = () => {
  const [qrCodes, setQrCodes] = useState([
    {
      id: 1,
      name: 'Morant',
      url: 'https://morant.com.mx',
      codeUrl: 'qr.codes/cKmY7j',
      scans: 3
    }
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleCreate = (newQR) => {
    const newQrCode = {
      id: qrCodes.length + 1,
      name: newQR.name,
      url: newQR.url,
      codeUrl: 'qr.codes/placeholder',
      scans: 0
    };
    setQrCodes([...qrCodes, newQrCode]);
    console.log('Nuevo QR:', newQR);
  };

  return (
    <Layout>
      <div>
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-green-700">Active QR Codes</h1>
          <CreateQRModal
            isOpen={isModalOpen}
            onClose={() => setIsModalOpen(false)}
            onCreate={handleCreate}
          />
          <button
            onClick={() => setIsModalOpen(true)}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
          >
            + Crear QR Code
          </button>
        </div>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {qrCodes.map((qr) => (
            <div key={qr.id} className="bg-white rounded-xl shadow-md p-5 flex flex-col items-center">
              <div className="w-32 h-32 mb-4 bg-gray-300 flex items-center justify-center text-gray-600">
                QR
              </div>
              <h2 className="text-xl font-semibold mb-1">{qr.name}</h2>
              <p className="text-sm text-gray-500 mb-2">{qr.codeUrl}</p>
              <a href={qr.url} target="_blank" rel="noreferrer" className="text-blue-600 text-sm underline mb-3">{qr.url}</a>
              <p className="text-sm text-gray-700 mb-3">Scans: {qr.scans}</p>
              <div className="flex gap-3">
                <button className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600">Editar</button>
                <button className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">Descargar</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </Layout>
  );
};

export default Dashboard;
