import React from 'react';

const QRCodeCard = ({ name, url, codeUrl, scans, onEdit, onDelete }) => {
  return (
    <div className="relative bg-white rounded-xl shadow-md p-5 flex flex-col items-center">
        <button
            className="absolute top-2 right-2 text-red-500 hover:text-red-700 text-xl font-bold"
            onClick={onDelete}
            title="Eliminar"
            > Ã— </button>
      <div className="w-32 h-32 mb-4 bg-gray-300 flex items-center justify-center text-gray-600">
        QR
      </div>
      <h2 className="text-xl font-semibold mb-1">{name}</h2>
      <p className="text-sm text-gray-500 mb-2">{codeUrl}</p>
      <a href={url} target="_blank" rel="noreferrer" className="text-blue-600 text-sm underline mb-3">
        {url}
      </a>
      <p className="text-sm text-gray-700 mb-3">Scans: {scans}</p>
      <div className="flex gap-3">
        <button className="bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
            onClick={onEdit}>Editar</button>
        <button className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600">Descargar</button>
        <button
            className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
            onClick={onDelete}
            >
            Eliminar
            </button>
      </div>
    </div>
  );
};

export default QRCodeCard;
