import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Layout from '../components/Nav/Layout';
import Spinner from '../components/utils/Spinner';
import httpService from '../services/httpService';

const FolderDetail = () => {
  const { id } = useParams(); // obtiene el ID desde la URL
  const [folder, setFolder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errorMsg, setErrorMsg] = useState('');

  useEffect(() => {
    const fetchFolder = async () => {
      try {
        setLoading(true);
        const response = await httpService.get(`/folder/${id}`);
        setFolder(response.data);
      } catch (error) {
        console.error('Error al obtener carpeta:', error);
        setErrorMsg('No se pudo cargar la carpeta.');
      } finally {
        setLoading(false);
      }
    };

    fetchFolder();
  }, [id]);

  return (
    <Layout>
      {loading && <Spinner />}
      {!loading && errorMsg && <p className="text-red-500 text-center mt-6">{errorMsg}</p>}
      {!loading && folder && (
        <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-md rounded">
          <h1 className="text-3xl font-bold text-green-700 mb-2">{folder.folderName}</h1>
          <h2 className="text-xl text-gray-600">Detalles de la carpeta</h2>
        </div>
      )}
    </Layout>
  );
};

export default FolderDetail;
